let jsonData;
const tierOne = document.querySelector(".tier--one"),
  navItem = document.querySelectorAll(".nav__item"),
  cardContainers = document.querySelectorAll(".tier__unitcards");
let state = "Terran",
  unitCardsOne = document.querySelector(".tier__unitcards--one"),
  unitCardsTwo = document.querySelector(".tier__unitcards--two"),
  unitCardsThree = document.querySelector(".tier__unitcards--three");
navItem.forEach((n) => {
  n.addEventListener("click", (e) => {
    n.classList.length < 2 &&
      navItem.forEach((n) => {
        n.classList.remove("nav__item--active");
      }),
      n.classList.add("nav__item--active"),
      (state = n.children[1].textContent),
      setTimeout((n) => {
        fetch("assets/units.json")
          .then((n) => n.json())
          .then((n) => {
            (jsonData = n),
              cardContainers.forEach((n) => {
                n.innerHTML = "";
              });
            const e = jsonData[state];
            Object.keys(e.tier_1).forEach((n) => {
              let t = unitCardsOne.appendChild(document.createElement("div"));
              t.classList.add("tier__unitcard"),
                console.log(e.tier_1[n].strong[1]),
                (t.innerHTML = `\n         <div class="tier__hero">\n           <h2>${n}</h2>\n           <img src="./assets/${e.tier_1[n].img_path}" />\n         </div>\n         <div class="tier__ability tier__strong">\n           <h3>strong</h3>\n           <ul>\n             <li>\n               <img\n                 src="./assets/${e.tier_1[n].strong[1]}.webp"\n                 alt="${e.tier_1[n].strong[1]}"\n               /><span>${e.tier_1[n].strong[1]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_1[n].strong[2]}.webp"\n                 alt="${e.tier_1[n].strong[2]}"\n               /><span>${e.tier_1[n].strong[2]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_1[n].strong[3]}.webp"\n                 alt="${e.tier_1[n].strong[3]}"\n               /><span>${e.tier_1[n].strong[3]}</span>\n             </li>\n           </ul>\n         </div>\n         <div class="tier__ability tier__weak">\n           <h3>weak</h3>\n           <ul>\n             <li>\n               <img\n                 src="./assets/${e.tier_1[n].weak[1]}.webp"\n                 alt="${e.tier_1[n].weak[1]}"\n               /><span>${e.tier_1[n].weak[1]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_1[n].weak[2]}.webp"\n                 alt="${e.tier_1[n].weak[2]}"\n               /><span>${e.tier_1[n].weak[2]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_1[n].weak[3]}.webp"\n                 alt="${e.tier_1[n].weak[3]}"\n               /><span>${e.tier_1[n].weak[3]}</span>\n             </li>\n           </ul>\n         </div>`);
            }),
              Object.keys(e.tier_2).forEach((n) => {
                let t = unitCardsTwo.appendChild(document.createElement("div"));
                t.classList.add("tier__unitcard"),
                  console.log(e.tier_2[n].strong[1]),
                  (t.innerHTML = `\n         <div class="tier__hero">\n           <h2>${n}</h2>\n           <img src="./assets/${e.tier_2[n].img_path}" />\n         </div>\n         <div class="tier__ability tier__strong">\n           <h3>strong</h3>\n           <ul>\n             <li>\n               <img\n                 src="./assets/${e.tier_2[n].strong[1]}.webp"\n                 alt="${e.tier_2[n].strong[1]}"\n               /><span>${e.tier_2[n].strong[1]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_2[n].strong[2]}.webp"\n                 alt="${e.tier_2[n].strong[2]}"\n               /><span>${e.tier_2[n].strong[2]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_2[n].strong[3]}.webp"\n                 alt="${e.tier_2[n].strong[3]}"\n               /><span>${e.tier_2[n].strong[3]}</span>\n             </li>\n           </ul>\n         </div>\n         <div class="tier__ability tier__weak">\n           <h3>weak</h3>\n           <ul>\n             <li>\n               <img\n                 src="./assets/${e.tier_2[n].weak[1]}.webp"\n                 alt="${e.tier_2[n].weak[1]}"\n               /><span>${e.tier_2[n].weak[1]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_2[n].weak[2]}.webp"\n                 alt="${e.tier_2[n].weak[2]}"\n               /><span>${e.tier_2[n].weak[2]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_2[n].weak[3]}.webp"\n                 alt="${e.tier_2[n].weak[3]}"\n               /><span>${e.tier_2[n].weak[3]}</span>\n             </li>\n           </ul>\n         </div>`);
              }),
              Object.keys(e.tier_3).forEach((n) => {
                let t = unitCardsThree.appendChild(
                  document.createElement("div")
                );
                t.classList.add("tier__unitcard"),
                  console.log(e.tier_3[n].strong[1]),
                  (t.innerHTML = `\n         <div class="tier__hero">\n           <h2>${n}</h2>\n           <img src="./assets/${e.tier_3[n].img_path}" />\n         </div>\n         <div class="tier__ability tier__strong">\n           <h3>strong</h3>\n           <ul>\n             <li>\n               <img\n                 src="./assets/${e.tier_3[n].strong[1]}.webp"\n                 alt="${e.tier_3[n].strong[1]}"\n               /><span>${e.tier_3[n].strong[1]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_3[n].strong[2]}.webp"\n                 alt="${e.tier_3[n].strong[2]}"\n               /><span>${e.tier_3[n].strong[2]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_3[n].strong[3]}.webp"\n                 alt="${e.tier_3[n].strong[3]}"\n               /><span>${e.tier_3[n].strong[3]}</span>\n             </li>\n           </ul>\n         </div>\n         <div class="tier__ability tier__weak">\n           <h3>weak</h3>\n           <ul>\n             <li>\n               <img\n                 src="./assets/${e.tier_3[n].weak[1]}.webp"\n                 alt="${e.tier_3[n].weak[1]}"\n               /><span>${e.tier_3[n].weak[1]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_3[n].weak[2]}.webp"\n                 alt="${e.tier_3[n].weak[2]}"\n               /><span>${e.tier_3[n].weak[2]}</span>\n             </li>\n             <li>\n               <img\n                 src="./assets/${e.tier_3[n].weak[3]}.webp"\n                 alt="${e.tier_3[n].weak[3]}"\n               /><span>${e.tier_3[n].weak[3]}</span>\n             </li>\n           </ul>\n         </div>`);
              });
          })
          .catch((n) => {
            console.error("Error fetching JSON:", n);
          });
      }, 50);
  });
}),
  setTimeout((n) => {
    navItem[0].click();
  }, 10);
